import React, { useState, useEffect } from "react";
import axios from 'axios';
import {Link} from 'react-router-dom';
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App() {

	const [notes, setNotes] = useState([]);
	const [Id, setId] = useState(0);

	useEffect(function(){
		async function getNotes(){
			const response = await axios.get("http://localhost:6969/getNotes");
			setNotes(response.data);

		}
		getNotes();
	},[]);



	async function AddNote(nNote){
		const response = await axios({
			method: "post",
			url: "http://localhost:6969/addNote",
			data:{
				_id:Id,
				title:nNote.title,
				content:nNote.content

			}
		});
		setId(Id+1);

		//setNotes(Object.entries(response.data));

	}



  async function addNote(newNote) {
	  await AddNote(newNote);
    setNotes(prevNotes => {
      return [...prevNotes, newNote];
    });
  }

  async function deleteNote(id) {

	await axios({
		method:"delete",
		url:`http://localhost:6969/deleteNote/${id}`,
	});

    setNotes(prevNotes => {
      return prevNotes.filter((noteItem) => {
        return noteItem._id !== id;
      });
    });
  }

  return (
    <div>
      <Header />
      <CreateArea onAdd={addNote} />
      {notes.map((noteItem) => {

        return (
          <Note
            key={noteItem._id}
            id={noteItem._id}
            title={noteItem.title}
            content={noteItem.content}
            onDelete={deleteNote}
          />
        );
      })}
      <Footer />
    </div>
  );
}

export default App;
